import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';
import { Orders } from 'src/app/model/user/Orders';
import { UserService } from 'src/app/service/user.service';

@Component({
  selector: 'app-view-order',
  templateUrl: './view-order.component.html',
  styleUrls: ['./view-order.component.scss']
})
export class ViewOrderComponent {
  public orders: Orders= new Orders; 
  orderNumber=null;
  orderName="";
  orderDate="";
  orderNumberRe=null;
  price:any;
  errors="";
  constructor(
    private formBuilder: FormBuilder,private userService: UserService,
    private router: Router
  ){}
  onClickSubmit():void {
    this.orders.email=localStorage.getItem("EMAIL")!;

    this.orders.orderNumber=this.orderNumber!;
    this.userService.getOrder(this.orders).subscribe((response) => {
        this.orderName="Order Name: "+response.orderName;
        this.price="Total: $"+response.price;
        this.orderDate="Order Date: "+response.orderDate;    
    },(error) => {
      console.error(error);
      this.errors="Cannot find Order";
    })

}
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Login</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background: linear-gradient(to bottom, #6C63FF, #1E90FF);
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
        }

        .container {
            background-color: rgba(255, 255, 255, 0.9);
            border-radius: 10px;
            padding: 20px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
            text-align: center;
            max-width: 700px;
        }

        h2 {
            font-size: 28px;
            color: #007BFF;
        }

        .form-control {
            margin-bottom: 20px;
            padding: 10px;
            border: 1px solid #ddd;
            border-radius: 5px;
            width: 100%;
        }

        .btn-primary {
            background-color: #007BFF;
            color: #fff;
            border: none;
            border-radius: 5px;
            padding: 10px 20px;
            font-size: 16px;
        }

        .btn-primary:hover {
            background-color: #0056b3;
        }

        a {
            text-decoration: none;
            color: #007BFF;
        }
        .sign-out{
    background-color: #007BFF;
    color: #fff;
    border: none;
    border-radius: 5px;
    padding: 10px 20px;
    font-size: 16px;
    float: right;
}
    </style>
</head>
<a href="/dashboard"><button class="sign-out">Sign out</button></a>
<body>
    <div class="container">
        <h2 class="fw-bold mb-4">Enter the Order Number</h2>
        <input type="text" [(ngModel)]="orderNumber"  class="form-control" placeholder="OrderNumber">
        <button class="btn btn-primary" (click)="onClickSubmit()" type="submit">Search</button> 
        <p class="mt-3"><a routerLink="/order-dashboard">Back to Order Dashboard</a></p>
        <p>{{orderName}}</p>
        <p>{{price}}</p>
        <p>{{orderDate}}</p>
    </div>
</body>
</html>
