import { Component } from '@angular/core';

@Component({
  selector: 'app-order-dashboard',
  templateUrl: './order-dashboard.component.html',
  styleUrls: ['./order-dashboard.component.scss']
})
export class OrderDashboardComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Dashboard</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background: linear-gradient(to bottom, #6C63FF, #1E90FF);
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
        }

        .container {
            background-color: rgba(255, 255, 255, 0.9);
            border-radius: 10px;
            padding: 20px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
            text-align: center;
        }

        h1 {
            font-size: 28px;
            color: #007BFF;
        }

        p {
            font-size: 18px;
            margin: 20px 0;
        }

        .btn {
            display: inline-block;
            padding: 10px 20px;
            background-color: #007BFF;
            color: #fff;
            text-decoration: none;
            margin: 10px;
            border-radius: 5px;
            font-size: 16px;
        }

        /* Additional styling for improved color scheme */
        .container {
            background-color: rgba(255, 255, 255, 0.9);
            border: 1px solid #ddd;
        }

        .btn {
            background-color: #1E90FF;
            transition: background-color 0.2s;
        }

        .btn:hover {
            background-color: #007BFF;
        }
        .sign-out{
    background-color: #007BFF;
    color: #fff;
    border: none;
    border-radius: 5px;
    padding: 10px 20px;
    font-size: 16px;
    float: right;
}
    </style>
</head>
<a href="/dashboard"><button class="sign-out">Sign out</button></a>
<body>
    <div class="container">
        <h1>Welcome to the Order Dashboard</h1>
        <p>Choose an option:</p>
        <a class="btn" routerLink="/view-order">View Order</a>
        <a class="btn" routerLink="/create-order">Create Order</a>
    </div>
</body>
</html>
